var bunyan = require('bunyan');

exports.logger = function (path) {
    if(!path){
        path = 'logs/index.log'
    }
    return bunyan.createLogger({
        name: 'shoppinglogger',
        streams: [{
            level: 'info',
            type: 'rotating-file',
            path: path,
            period: '1d', // daily rotation
            count: 3650 // keep 3 back copies
        }]
    });
};